mapper.update:
update saleboard set sb_title = #{sb_title}, sb_category = #{sb_category}, sb_startprice = #{sb_startprice},
sb_count = #{sb_count}, sb_contents = #{sb_contents}, sb_date = default, sb_timer = #{sb_timer}, sb_buylevel = #{sb_buylevel}
where sb_num = #{sb_num}

update saleboard set sb_title = #{sb_title}, sb_category = #{sb_category}, sb_startprice = #{sb_startprice},
sb_count = #{sb_count}, sb_contents = #{sb_contents}, sb_date = default, sb_timer = #{sb_timer}, sb_buylevel = #{sb_buylevel}
where sb_num = #{sb_num}

mapper.select:
SELECT COUNT(*) FROM saleboard WHERE sb_scope = 1 and sb_salekind = 3
and sb_category = #{sb_category}
and (sb_category like concat('%',#{keyWord},'%') or sb_title like concat('%',#{keyWord},'%') or sb_contents like concat('%',#{keyWord},'%') or sb_local like concat('%',#{keyWord},'%'))
