mapper.select:
select * from saleboard left join boardfile on sb_num = bf_sb_num where sb_scope = 1 and sb_salekind =1
and sb_category = #{sb_category}
and (sb_category like concat('%',#{keyWord},'%') or sb_title like concat('%',#{keyWord},'%') or sb_contents like concat('%',#{keyWord},'%') or sb_local like concat('%',#{keyWord},'%'))
order by sb_num desc limit #{startIdx}, #{listCnt}

SELECT COUNT(*) FROM saleboard WHERE sb_scope = 1 and sb_salekind = 1
and sb_category = #{sb_category}
and (sb_category like concat('%',#{sb_category},'%') or sb_title like concat('%',#{keyWord},'%') or sb_contents like concat('%',#{keyWord},'%') or sb_local like concat('%',#{keyWord},'%'))

select * from saleboard left join boardfile on sb_num = bf_sb_num where sb_scope = 1 and sb_salekind =2
and sb_category = #{sb_category}
and (sb_category like concat('%',#{keyWord},'%') or sb_title like concat('%',#{keyWord},'%') or sb_contents like concat('%',#{keyWord},'%') or sb_local like concat('%',#{keyWord},'%'))
order by sb_num desc LIMIT #{listCnt} OFFSET #{startIdx}

select * from saleboard left join boardfile on sb_num = bf_sb_num
         where (sb_title like concat('%',#{keyWord},'%') or sb_contents like concat ('%',#{keyWord},'%') or sb_category like concat ('%',#{keyWord},'%')) and sb_scope = 1
         order by sb_date desc limit #{startIdx}, #{listCnt}

SELECT COUNT(*) FROM saleboard WHERE sb_scope = 1 and sb_salekind = 2
and sb_category = #{sb_category}
and (sb_category like concat('%',#{keyWord},'%') or sb_title like concat('%',#{keyWord},'%') or sb_contents like concat('%',#{keyWord},'%') or sb_local like concat('%',#{keyWord},'%'))
